正常消息轮发的问题生产者12345678910111213141516171819public function noPrefetchCount()&#123; $connection = new AMQPStreamConnection(&apos;127.0.0.1&apos;, 5672, &apos;guest&apos;, &apos;guest&apos;); $channel = $connection-&gt;channel(); // 声明 $exchange = &apos;prefetch_exchange&apos;; $queueName = &apos;test_queue&apos;; // 声明交换机 $channel-&gt;exchange_declare($exchange, &apos;direct&apos;, false, true, false); // 声明队列 $channel-&gt;queue_declare($queueName, false, true, false, false); $channel-&gt;queue_bind($queueName, $exchange); for ($i=0; $i&lt;10; $i++) &#123; $message = new AMQPMessage(&apos;hello world--&apos; . $i, [&apos;content_type&apos; =&gt; &apos;text/plain&apos;, &apos;delivery_mode&apos; =&gt; AMQPMessage::DELIVERY_MODE_PERSISTENT,]); $channel-&gt;basic_publish($message, $exchange, null, true); &#125; $channel-&gt;close(); $connection-&gt;close();&#125; 消费者1通过sleep(10)来模拟消费者1的复杂业务1234567891011121314151617181920212223242526272829303132333435363738394041424344454647484950515253545556575859606162636465&lt;?phpnamespace App\Console\Commands;use Illuminate\Console\Command;use PhpAmqpLib\Connection\AMQPStreamConnection;use PhpAmqpLib\Message\AMQPMessage;use PhpAmqpLib\Wire\AMQPTable;class Consumer4 extends Command&#123; /** * The name and signature of the console command. * * @var string */ protected $signature = &apos;Consumer4&apos;; /** * The console command description. * * @var string */ protected $description = &apos;测试消费者&apos;; public function __construct() &#123; parent::__construct(); &#125; /** * Execute the console command. * * @return mixed */ public function handle() &#123; $connection = new AMQPStreamConnection(&apos;127.0.0.1&apos;, 5672, &apos;guest&apos;, &apos;guest&apos;); $channel = $connection-&gt;channel(); // 声明 $exchange = &apos;prefetch_exchange&apos;; $queueName = &apos;test_queue&apos;; // 声明交换机 $channel-&gt;exchange_declare($exchange, &apos;direct&apos;, false, true, false); // 声明队列 $channel-&gt;queue_declare($queueName, false, true, false, false); $channel-&gt;queue_bind($queueName, $exchange); /** * @param \PhpAmqpLib\Message\AMQPMessage $message */ $backCall = function ($message) &#123; echo &quot;
--------
&quot;; echo $message-&gt;body; //echo $message-&gt;delivery_info[&apos;delivery_tag&apos;]; echo &quot;
